package switchCase;

public class switchUsingString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String level = "Expert";
		switch(level) {
		case "Beginner": System.out.println("level is Beginner");
		break;
		case "Advenced": System.out.println("level is Advanced");
		break;
		case "Expert": System.out.println("level is expert");
		}

	}

}
